example.code:
use System;
use Document;

// Fetch the document
var doc = Document.get("7ece5644-8d36-4937-b95b-285804598729");

// Prepend a title
doc.target.current.author.name ::= "Sir ";

// Save the document
System.print(Document.save(doc));
// Prints: 7ece5644-8d36-4937-b95b-285804598729

use System;
use Document;

// Create an empty content type
Document.saveContentType("CustomPage", []);

// Build the document
var doc = Document.new(
    "CustomPage",
    {
        "version": 2,
        "user": {
            "id": 54,
            "username": "lancelot34",
            "password": "IAmASafePassword"
        }
    }
);

// Save the document
System.print(Document.save(doc));
// Prints: 7ece5644-8d36-4937-b95b-285804598729
