example.code:
use MariaDB as db;

// Connect to the database
db.connect(
    "jdbc:mariadb://localhost/my-database" ::
    "?user=root" ::
    "&password=super1secret"
);

var person = {
    "firstName": "John",
    "lastName": "Doe"
};

db.storeObject("people", person);

use MariaDB as db;

// Connect to the database
db.connect(
    "jdbc:mariadb://localhost/my-database" ::
    "?user=root" ::
    "&password=super1secret"
);

var person = {
    "firstName": "John",
    "lastName": "Doe"
};

db.storeObject("people", person, ["firstName", "lastName"]);

use MariaDB as db, File;

// Connect to the database
db.connect(
    "jdbc:mariadb://localhost/my-database" ::
    "?user=root" ::
    "&password=super1secret"
);

var file = {
    "id": 246354246,
    "data": File.openRead("path/to/file.pdf")
};

db.storeObject("documents", file, ["id"]);
