example.code:
use MSSQL, System;

// Connect to the database
MSSQL.connect(
    "jdbc:sqlserver://localhost" ::
    ";database=Database" ::
    ";user=MyUserName" ::
    ";password=MyPassword"
);

var object = MSSQL.getObject("people", {"id": 5346});

var json = System.toJSON(object, true);
System.print(json);

use MSSQL, System;

// Connect to the database
MSSQL.connect(
    "jdbc:sqlserver://localhost" ::
    ";database=Database" ::
    ";user=MyUserName" ::
    ";password=MyPassword"
);

var example = {
    "firstName": "John",
    "lastName": "Doe"
};

var row = MSSQL.getObject("people", example);

var json = System.toJSON(row, true);
System.print(json);
