example.code:
use MSSQL;

// Connect to the database
MSSQL.connect("jdbc:sqlserver://localhost;database=Database;instance=sqlexpress;user=MyUserName;password=MyPassword");

var document = {
    "title": "Report",
    "publicationDate" : MSSQL.toDate(Date.now())
};

MSSQL.storeObject("publications", document);
