example.code:
use System;
use REST;
use XML;
var bodyStr = "< ?xml version=\"1.0\" encoding=\"UTF-8\"?> < test name=\"xillio\">content< /test>";
var body = XML.fromString(bodyStr);
var response = REST.post("http://whatever.com/uri", {"timeout" : 3000}, body);
System.print(response);

use System;
use REST;
use XML;
var body = "just test text";
var contentType = "application/vnd.emc.documentum+json";
var response = REST.post("http://whatever.com/uri", {"timeout" : 3000}, body, contentType);
System.print(response);
