example.code:
use System, String;

var url = "http://www.xillio.com/wp-content/uploads/screenshot-ns-website.png";
System.print(String.regex(url, "http.*/(.*?)\\.(.*?)"));

/*
Outputs:
    [
        "http://www.xillio.com/wp-content/uploads/screenshot-ns-website.png",
        "screenshot-ns-website",
        "png"
    ]
*/
